import React, { useState, useEffect } from 'react'
import Cookies from 'js-cookie'
import axios from 'axios';
import AuthContext from './AuthContext'
// import Router, { useRouter } from 'next/router'

// api here is an axios instance which has the baseURL set according to the env.
import api from '../../services/api';

export default ({children }) => {

    const [user, setUser] = useState(null)
    const [loading, setLoading] = useState(true)

    useEffect(() => {
        async function loadUserFromCookies() {
            const token = Cookies.get('token')
            if (token) {
                console.log("Got a token in the cookies, let's see if it is valid")
                api.defaults.headers.Authorization = `Bearer ${token}`
                const { data: user } = await api.get('users/me')
                if (user) setUser(user);
            }
            setLoading(false)
        }
        loadUserFromCookies()
    }, [])

    const login = async (email, password) => {
        const params = new URLSearchParams();
        params.append('username', email);
        params.append('password',password);
        const { data: token } = await api.post('auth/token', params)
        if (token) {
            console.log("Got token")
            Cookies.set('token', token, { expires: 60 })
            console.log(token)
            // api.defaults.headers.Authorization = `Bearer ${token.token}`
            const header = {headers: {Authorization: `Bearer ${token}`}}
            // const { data: user } = await axios.get('users/me', header)
            const { data: user } = await api.get('users/me', header)
            setUser(user)
            console.log("Got user", user)
        }
    }

    const logout = (email, password) => {
        Cookies.remove('token')
        setUser(null)
        delete api.defaults.headers.Authorization
        window.location.pathname = '/login'
    }


    return (
        <AuthContext.Provider value={{ isAuthenticated: !!user, isLoading: !!loading, user, login, logout }}>
            {children}
        </AuthContext.Provider>
    )
}
