import { Formik, Field, Form } from 'formik';
import React, { useState } from 'react';

export default function DeleteRequestCard() {
  const [numberCharacters, setNumberCharacters] = useState(0);
  const handleTextareaChange = (value) => {
    setNumberCharacters(value.length);
  };

  return (
    <div className="p-4 mt-8 bg-white border-0 border-gray-800 rounded-md shadow-xl">
      <div className="flex items-baseline">
        <div className="text-4xl font-semibold">Jan R.</div>
        <div className="ml-8 text-base font-extrabold">
          submitted 1 hour ago
        </div>
        <div className="flex-row ml-auto">
          <button
            type="button"
            className="hover:bg-red-600 w-full p-4 text-xl text-white bg-red-500 rounded-md"
            onClick={() => setMessageModalState(true)}
          >
            Delete
          </button>
          <div className="text-base font-semibold">
            10 people have reached out already!
          </div>
        </div>
      </div>

      <Formik
        initialValues={{}}
        onSubmit={(values) => {
          // updateUser(values)
          //   .then(() => {
          //     setAlertOpen(true);
          //     setAlertText('Profile successfully updated');
          //   })
          //   .catch((error) => {
          //     setAlertOpen(true);
          //     setAlertText(error.message);
          //   });
        }}
      >
        <Form className="w-full mx-auto mt-4">
          <div className="w-5/6 mt-4 ml-0 overflow-hidden text-lg">
            <div>
              <label htmlFor="Message" className="font-semibold">
                Description text:
              </label>
              <Field as="textarea" name="description" rows={5}>
                {({
                  field, // { name, value, onChange, onBlur }
                  form: { handleChange }, // also values, setXXXX, handleXXXX, dirty, isValid, status, etc.
                }) => (
                  <div>
                    <textarea
                      type="text"
                      rows={6}
                      placeholder="Description"
                      className="w-full p-2 border-2"
                      {...field}
                      onChange={(e) => {
                        handleTextareaChange(e.target.value);
                        handleChange(e.target.value);
                      }}
                    />
                  </div>
                )}
              </Field>
              <div className="text-right">{numberCharacters}/250</div>
            </div>
          </div>
          <div className="flex justify-start mt-4 text-xl">
            <Field
              as="select"
              name="lcSubmissions"
              className="rounded-xl border-crown-of-thorns-500 text-crown-of-thorns-500 p-2 border-2"
            >
              0<option value="0">0-20 LC submissions</option>1
              <option value="1">20-100 LC submissions</option>2
              <option value="2">100+ LC submissions</option>{' '}
            </Field>
            <Field
              as="select"
              name="TimeToInterview"
              className="rounded-xl border-yukon-gold-500 text-yukon-gold-500 p-2 ml-4 border-2"
            >
              0<option value="0">Interview in 2 weeks</option>1
              <option value="1">Interview in 1 month</option>2
              <option value="2">Interview in 3 months</option>{' '}
              <option value="3">Interview in 6 months</option>{' '}
            </Field>
          </div>
          <div className="flex justify-end mt-8">
            <button
              type="button"
              className="hover:bg-gray-600 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 flex justify-center px-4 py-2 text-2xl font-medium text-white bg-gray-500 border border-transparent rounded-md shadow-sm"
            >
              Cancel
            </button>
            <button
              type="submit"
              className="bg-blumine-500 hover:bg-blumine-600 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 flex justify-center px-4 py-2 ml-8 text-2xl font-medium text-white border border-transparent rounded-md shadow-sm"
            >
              Submit Changes
            </button>
          </div>
        </Form>
      </Formik>
    </div>
  );
}
