import React, { useState } from 'react';
import Head from 'next/head';

export default function Home() {
  const [fileName, setFileName] = useState<string | null>(null);
  const [fileContent, setFileContent] = useState<string | null>(null);
  const [fileParsedContent, setFileParsedContent] = useState<string | null>(
    null
  );

  const parseFile = (file: string) => {
    const splittedFile = file.split(/\r?\n/g);
    console.log(splittedFile.length);
    return splittedFile;
  };

  const handleFileUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const target = e.target as HTMLInputElement;
    const file: File = (target.files as FileList)[0];
    const reader = new FileReader();
    reader.readAsText(file);
    reader.onload = () => {
      setFileContent(reader.result as string);
      // setFileParsedContent(parseFile(reader.result as string));
    };

    reader.onerror = () => {
      console.log(reader.error);
    };

    setFileName(file.name);
  };
  return (
    <div>
      <Head>
        <title>Kindle Clippings Parser</title>
        {/* <link rel="icon" href="/favicon.ico" /> */}
      </Head>

      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="max-w-3xl mx-auto">
          <h1 className="mt-8 text-2xl text-center">
            Welcome friends of LibGen!
          </h1>
          <div className="mt-8">
            <input type="file" accept=".txt" onChange={handleFileUpload} />
            Uploaded File: {fileName}
            <div className="mt-4">
              <h2>File: </h2>
              {fileContent}
            </div>
            <div className="mt-4">
              <h2>Parsed File:</h2>
              {fileParsedContent}
            </div>
          </div>
        </div>
      </main>

      <footer />
    </div>
  );
}
