import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCaretDown } from '@fortawesome/free-solid-svg-icons';
import { useState } from 'react';
import RequestCard from '../components/RequestCard';
import SignupModal from '../components/SignupModal';
import SendMessageModal from '../components/SendMessageModal';
import NewRequestCard from '../components/NewRequestCard';

export default function Home() {
  const [signupModalState, setSignUpModalState] = useState(false);
  const [messageModalState, setMessageModalState] = useState(false);
  const [loggedIn, setLoggedIn] = useState(false);
  const [newRequestState, setNewRequestState] = useState(false);

  const clickCreateNewRequest = () => {
    if (loggedIn) {
      setNewRequestState(true);
    } else {
      setSignUpModalState(true);
    }
  };

  return (
    <div className="sm:px-6 lg:px-8 max-w-screen-xl mx-auto">
      <div className="flex-col justify-between min-h-screen">
        <header className="">
          <nav className="flex items-baseline justify-between w-full mt-4 text-xl">
            <div className="text-3xl">leetcodebuddy.xyz</div>
            <div>
              {loggedIn && (
                <button
                  type="button"
                  className="bg-blumine-500 hover:bg-blumine-600 p-4 mr-8 text-white rounded-md"
                >
                  My Posts
                </button>
              )}
              <button
                type="button"
                className="bg-blumine-500 hover:bg-blumine-600 p-4 text-white rounded-md"
              >
                Login
              </button>
            </div>
          </nav>
        </header>
        <main className="mt-14">
          {signupModalState && (
            <SignupModal setModalState={setSignUpModalState} />
          )}
          {messageModalState && (
            <SendMessageModal postId={3} setModalState={setMessageModalState} />
          )}
          <div className="font-courier p-2 text-2xl text-white bg-gray-500">
            The preparation for Software Engineering interviews is exhausting.
            Grinding Leetcode alone is not fun. <br /> <br />
            Shareing the journey with a partner and support each other makes a
            difference. This platform will help you to find the right partner
            for you. Click here to read our story. You need an account to create
            a new post. No account is needed to send a message to other people.
          </div>
          <div className="flex justify-between w-full mt-10">
            <div className="flex justify-between">
              <div>
                <button
                  type="button"
                  className="p-4 text-xl text-white bg-gray-400 rounded-md"
                >
                  LC submissions
                  <FontAwesomeIcon
                    className=" ml-2 text-xl"
                    icon={faCaretDown}
                  />
                </button>
              </div>
              <div className="ml-8">
                <button
                  type="button"
                  className="p-4 text-xl text-white bg-gray-400 rounded-md"
                >
                  Interview date
                  <FontAwesomeIcon
                    className=" ml-2 text-xl"
                    icon={faCaretDown}
                  />
                </button>
              </div>
            </div>
            <div>
              <button
                type="button"
                className="bg-blumine-500 hover:bg-blumine-600 p-4 text-xl text-white rounded-md"
                onClick={clickCreateNewRequest}
              >
                Create new request
              </button>
            </div>
          </div>
          <div className="h-1 mt-4 bg-black" />
          <div className="mt-4">
            {newRequestState && <NewRequestCard />}
            <RequestCard
              author="Jan R."
              submissionDate="1 hour ago"
              descriptionText="Blba bla blabla"
              interviweTimeline={2}
              LCExperience={2}
              setMessageModalState={setMessageModalState}
            />
            {/* <RequestCard />
            <RequestCard />
            <RequestCard />
            <RequestCard />
            <RequestCard />
            <RequestCard />
            <RequestCard /> */}
          </div>
        </main>
        <footer className="flex justify-between my-8">
          <div>From students for students with lots of LUUVE</div>
          <div>Contact</div>
        </footer>
      </div>
    </div>
  );
}
