import React, { SetStateAction } from 'react';

interface RequestCardProps {
  setMessageModalState: React.Dispatch<SetStateAction<boolean>>;
  author: string;
  LCExperience: number;
  InterviewTimeline: number;
  descriptionText: string;
  submissionDate: Date;
}

export default function RequestCard({
  setMessageModalState,
  author,
  LCExperience,
  InterviewTimeline,
  descriptionText,
  submissionDate,
}: RequestCardProps) {
  return (
    <div className="p-4 mt-8 bg-white border-0 border-gray-800 rounded-md shadow-xl">
      <div className="flex items-baseline">
        <div className="text-4xl font-semibold">Jan R.</div>
        <div className="ml-8 text-base font-extrabold">
          submitted {submissionDate}
        </div>
        <div className="flex-row ml-auto">
          <button
            type="button"
            className="bg-blumine-500 hover:bg-blumine-600 w-full p-4 text-xl text-white rounded-md"
            onClick={() => setMessageModalState(true)}
          >
            Message
          </button>
          <div className="text-base font-semibold">
            10 people have reached out already!
          </div>
        </div>
      </div>
      <div className="w-5/6 mt-4 ml-0 overflow-hidden text-lg">
        Hi, I am looking for a partner to bla bla. Ideally we text each other in
        morning once a week. Lorem upsum Lorem upsum Lorem upsum Lorem upsum
        Lorem upsum Lorem upsum Lorem upsum Lorem upsum Lorem upsum
      </div>
      <div className="flex justify-start mt-4 text-xl">
        <div className="rounded-xl border-crown-of-thorns-500 text-crown-of-thorns-500 p-2 border-2">
          20 LC submissions
        </div>
        <div className="rounded-xl border-yukon-gold-500 text-yukon-gold-500 p-2 ml-4 border-2">
          Interview in 3 months
        </div>
        <div className="rounded-xl border-jewel-500 text-jewel-500 p-2 ml-4 border-2">
          -3 hours time difference
        </div>
      </div>
    </div>
  );
}
