import { Formik } from 'formik';

export default function NewRequest() {
  return (
    <div className="mb-64">
      <div className="p-4 mt-8 bg-white border-0 border-gray-800 rounded-md shadow-xl">
        <div className="flex items-baseline">
          <div className="text-4xl font-semibold">Jan R.</div>
        </div>
        <div className="w-5/6 mt-4 ml-0 overflow-hidden text-lg">
          <Formik
            initialValues={{
              fullName,
              // email,
              company,
              jobTitle,
              password: '',
            }}
            onSubmit={(values) => {
              updateUser(values)
                .then(() => {
                  setAlertOpen(true);
                  setAlertText('Profile successfully updated');
                })
                .catch((error) => {
                  setAlertOpen(true);
                  setAlertText(error.message);
                });
            }}
            validate={validate}
          >
            {({
              errors,
              touched,
              values,
              handleChange,
              handleBlur,
              handleSubmit,
            }) => (
              <form className="w-full mx-auto mt-4" onSubmit={handleSubmit}>
                {/* <input type="hidden" name="remember" value="true" /> */}
                <div className="bg-nord-highlight1 p-8 rounded-md">
                  <div className="">
                    <input
                      aria-label="Full name"
                      name="fullName"
                      type="text"
                      required
                      className="focus:outline-none focus:shadow-outline-blue focus:border-blue-300 focus:z-10 sm:text-sm sm:leading-5 relative block w-full px-3 py-2 text-gray-900 placeholder-gray-400 border border-gray-300 rounded-md appearance-none"
                      placeholder="Fullname"
                      onChange={handleChange}
                      onBlur={handleBlur}
                      value={values.fullName}
                    />
                    {errors.fullName && touched.fullName ? (
                      <ErrorMsg message={errors.fullName} />
                    ) : null}
                  </div>
                  {/* <div className="mt-4">
                  <input
                    aria-label="Email address"
                    name="email"
                    type="email"
                    required
                    className="focus:outline-none focus:shadow-outline-blue focus:border-blue-300 focus:z-10 sm:text-sm sm:leading-5 relative block w-full px-3 py-2 text-gray-900 placeholder-gray-400 border border-gray-300 rounded-md appearance-none"
                    placeholder="Email address"
                    onChange={handleChange}
                    onBlur={handleBlur}
                    value={values.email}
                  />
                  {errors.email && touched.email ? (
                    <ErrorMsg message={errors.email} />
                  ) : null}
                </div> */}
                  <div className="mt-4">
                    <input
                      aria-label="Company"
                      name="company"
                      type="text"
                      className="focus:outline-none focus:shadow-outline-blue focus:border-blue-300 focus:z-10 sm:text-sm sm:leading-5 relative block w-full px-3 py-2 text-gray-900 placeholder-gray-400 border border-gray-300 rounded-md appearance-none"
                      placeholder="Company or Institution (optional)"
                      onChange={handleChange}
                      onBlur={handleBlur}
                      value={values.company}
                    />
                    {errors.company && touched.company ? (
                      <ErrorMsg message={errors.company} />
                    ) : null}
                  </div>
                  <div className="mt-4">
                    <input
                      aria-label="Job title"
                      name="jobTitle"
                      type="text"
                      className="focus:outline-none focus:shadow-outline-blue focus:border-blue-300 focus:z-10 sm:text-sm sm:leading-5 relative block w-full px-3 py-2 text-gray-900 placeholder-gray-400 border border-gray-300 rounded-md appearance-none"
                      placeholder="Job title (optional)"
                      onChange={handleChange}
                      onBlur={handleBlur}
                      value={values.jobTitle}
                    />
                    {errors.jobTitle && touched.jobTitle ? (
                      <ErrorMsg message={errors.jobTitle} />
                    ) : null}
                  </div>
                  <div className=" mt-4">
                    <input
                      aria-label="Password"
                      name="password"
                      type="password"
                      className="focus:outline-none focus:shadow-outline-blue focus:border-blue-300 focus:z-10 sm:text-sm sm:leading-5 relative block w-full px-3 py-2 text-gray-900 placeholder-gray-400 border border-gray-300 rounded-md appearance-none"
                      placeholder="New password (leave blank if you don't want to change it.)"
                      onChange={handleChange}
                      onBlur={handleBlur}
                    />
                  </div>
                </div>

                <div className="w-3/8 float-right mt-10 mr-8">
                  <button
                    type="submit"
                    className="group bg-nord-highlight2 hover:bg-nord-highlight1 focus:outline-none focus:border-indigo-700 focus:shadow-outline-indigo active:bg-indigo-700 relative flex justify-center w-full px-4 py-2 text-sm font-medium leading-5 text-white transition duration-150 ease-in-out border border-transparent rounded-md"
                  >
                    Update Profile
                  </button>
                </div>
              </form>
            )}
          </Formik>

          <div>
            <label htmlFor="Message" className="font-semibold">
              Description text:
            </label>
            <textarea className="w-full border-2" rows={5}>
              asdasd
            </textarea>
            <div className="text-right">1/140</div>
          </div>
        </div>
        <div className="flex justify-start mt-4 text-xl">
          <div className="rounded-xl border-crown-of-thorns-500 text-crown-of-thorns-500 p-2 border-2">
            20 LC submissions
          </div>

          <div className="rounded-xl border-yukon-gold-500 text-yukon-gold-500 p-2 ml-4 border-2">
            Interview in 3 months
          </div>
        </div>
      </div>
      <div className="flex justify-end mt-8">
        <button
          type="submit"
          className="hover:bg-blumine-600 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 flex justify-center px-4 py-2 text-2xl font-medium text-white bg-red-500 border border-transparent rounded-md shadow-sm"
        >
          Cancel
        </button>
        <button
          type="submit"
          className="bg-blumine-500 hover:bg-blumine-600 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 flex justify-center px-4 py-2 ml-8 text-2xl font-medium text-white border border-transparent rounded-md shadow-sm"
        >
          Submit
        </button>
      </div>
    </div>
  );
}
