import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCaretDown } from '@fortawesome/free-solid-svg-icons';
import { useState } from 'react';
import { Listbox, Transition } from '@headlessui/react';
import RequestCard from '../components/RequestCard';
import SignupModal from '../components/SignupModal';
import SendMessageModal from '../components/SendMessageModal';
import NewRequestCard from '../components/NewRequestCard';

const interviewTimeline = [
  'Interview in 2 weeks',
  'Interview in 4 weeks',
  'Interview in 3 months',
  'Interview in 6 months',
  'all',
];

export default function Home() {
  const [signupModalState, setSignUpModalState] = useState(false);
  const [messageModalState, setMessageModalState] = useState(false);
  const [loggedIn, setLoggedIn] = useState(false);
  const [newRequestState, setNewRequestState] = useState(false);
  const [selectedInterviewTimeline, setSelectedInterviewTimeline] = useState(
    interviewTimeline[5]
  );

  const clickCreateNewRequest = () => {
    if (loggedIn) {
      setNewRequestState(true);
    } else {
      setSignUpModalState(true);
    }
  };

  return (
    <div className="sm:px-6 lg:px-8 max-w-screen-xl mx-auto">
      <div className="flex-col justify-between min-h-screen">
        <header className="">
          <nav className="flex items-baseline justify-between w-full mt-4 text-xl">
            <div className="text-3xl">leetcodebuddy.xyz</div>
            <div>
              {loggedIn && (
                <button
                  type="button"
                  className="bg-blumine-500 hover:bg-blumine-600 p-4 mr-8 text-white rounded-md"
                >
                  My Posts
                </button>
              )}
              <button
                type="button"
                className="bg-blumine-500 hover:bg-blumine-600 p-4 text-white rounded-md"
              >
                Login
              </button>
            </div>
          </nav>
        </header>
        <main className="mt-14">
          {signupModalState && (
            <SignupModal setModalState={setSignUpModalState} />
          )}
          {messageModalState && (
            <SendMessageModal postId={3} setModalState={setMessageModalState} />
          )}
          <div className="font-courier p-2 text-2xl text-white bg-gray-500">
            The preparation for Software Engineering interviews is exhausting.
            Grinding Leetcode alone is not fun. <br /> <br />
            Shareing the journey with a partner and support each other makes a
            difference. This platform will help you to find the right partner
            for you. Click here to read our story. You need an account to create
            a new post. No account is needed to send a message to other people.
          </div>
          <div className="flex justify-between w-full mt-10">
            {/* <div className="flex justify-between">
              <div> */}
            {/* {/* <button
                  type="button"
                  className="p-4 text-xl text-white bg-gray-400 rounded-md"
                > */}
            {/* LC submissions
                  <FontAwesomeIcon
                    className=" ml-2 text-xl"
                    icon={faCaretDown}
                  /> */}
            {/* </button> */}
            {/* <select
                  name="cars"
                  id="cars"
                  className=" text-2xl text-white bg-gray-400 rounded-md"
                >
                  <option value="volvo">Volvo</option>
                  <option value="saab">Saab</option>
                  <option value="mercedes">Mercedes</option>
                  <option value="audi">Audi</option>
                </select> */}

            <Listbox
              as="div"
              className="space-y-1"
              value={selectedInterviewTimeline}
              onChange={setSelectedInterviewTimeline}
            >
              {({ open }) => (
                <>
                  <Listbox.Label className="block text-sm font-medium leading-5 text-gray-700">
                    Assigned to
                  </Listbox.Label>
                  <div className="relative">
                    <span className="inline-block w-full rounded-md shadow-sm">
                      <Listbox.Button className="focus:outline-none focus:shadow-outline-blue focus:border-blue-300 sm:text-sm sm:leading-5 relative w-full py-2 pl-3 pr-10 text-left transition duration-150 ease-in-out bg-white border border-gray-300 rounded-md cursor-default">
                        <span className="block truncate">
                          {selectedInterviewTimeline}
                        </span>
                        <span className="absolute inset-y-0 right-0 flex items-center pr-2 pointer-events-none">
                          <svg
                            className="w-5 h-5 text-gray-400"
                            viewBox="0 0 20 20"
                            fill="none"
                            stroke="currentColor"
                          >
                            <path
                              d="M7 7l3-3 3 3m0 6l-3 3-3-3"
                              strokeWidth="1.5"
                              strokeLinecap="round"
                              strokeLinejoin="round"
                            />
                          </svg>
                        </span>
                      </Listbox.Button>
                    </span>

                    <Transition
                      show={open}
                      leave="transition ease-in duration-100"
                      leaveFrom="opacity-100"
                      leaveTo="opacity-0"
                      className="absolute w-full mt-1 bg-white rounded-md shadow-lg"
                    >
                      <Listbox.Options
                        static
                        className="max-h-60 focus:outline-none sm:text-sm sm:leading-5 py-1 overflow-auto text-base leading-6 rounded-md shadow-xs"
                      >
                        {interviewTimeline.map((person) => (
                          <Listbox.Option key={person} value={person}>
                            {({ selected, active }) => (
                              <div
                                className={`${
                                  active
                                    ? 'text-white bg-blue-600'
                                    : 'text-gray-900'
                                } cursor-default select-none relative py-2 pl-8 pr-4`}
                              >
                                <span
                                  className={`${
                                    selected ? 'font-semibold' : 'font-normal'
                                  } block truncate`}
                                >
                                  {person}
                                </span>
                                {selected && (
                                  <span
                                    className={`${
                                      active ? 'text-white' : 'text-blue-600'
                                    } absolute inset-y-0 left-0 flex items-center pl-1.5`}
                                  >
                                    <svg
                                      className="w-5 h-5"
                                      xmlns="http://www.w3.org/2000/svg"
                                      viewBox="0 0 20 20"
                                      fill="currentColor"
                                    >
                                      <path
                                        fillRule="evenodd"
                                        d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z"
                                        clipRule="evenodd"
                                      />
                                    </svg>
                                  </span>
                                )}
                              </div>
                            )}
                          </Listbox.Option>
                        ))}
                      </Listbox.Options>
                    </Transition>
                  </div>
                </>
              )}
            </Listbox>
            {/* </div> */}
            <div className="ml-8">
              <button
                type="button"
                className="p-4 text-xl text-white bg-gray-400 rounded-md"
              >
                Interview date
                <FontAwesomeIcon className=" ml-2 text-xl" icon={faCaretDown} />
              </button>
            </div>
            {/* </div> */}
            <div>
              <button
                type="button"
                className="bg-blumine-500 hover:bg-blumine-600 p-4 text-xl text-white rounded-md"
                onClick={clickCreateNewRequest}
              >
                Create new request
              </button>
            </div>
          </div>
          <div className="h-1 mt-4 bg-black" />
          <div className="mt-4">
            {newRequestState && <NewRequestCard />}
            <RequestCard
              author="Jan R."
              submissionDate={Date.now()}
              descriptionText="Blba bla blabla"
              interviewTimeline={2}
              LCExperience={2}
              setMessageModalState={setMessageModalState}
              timezone={3}
              numberReplies={10}
            />
            {/* <RequestCard />
            <RequestCard />
            <RequestCard />
            <RequestCard />
            <RequestCard />
            <RequestCard />
            <RequestCard /> */}
          </div>
        </main>
        <footer className="flex justify-between my-8">
          <div>From students for students with lots of LUUVE</div>
          <div>Contact</div>
        </footer>
      </div>
    </div>
  );
}
